// src/pages/Market.js
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { getMarketData } from '../api';

const MarketContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem 1rem;
`;

const Title = styled.h1`
  color: ${props => props.theme.colors.primary};
  text-align: center;
  margin-bottom: 2rem;
`;

const ProductGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 2rem;
`;

const ProductCard = styled.div`
  border: 1px solid #ddd;
  border-radius: 8px;
  padding: 1rem;
  text-align: center;
`;

const ProductImage = styled.img`
  max-width: 100%;
  height: auto;
`;

const Market = () => {
  const [marketData, setMarketData] = useState(null);

  useEffect(() => {
    const fetchMarketData = async () => {
      const data = await getMarketData();
      setMarketData(data);
    };
    fetchMarketData();
  }, []);

  return (
    <MarketContainer>
      <Title>Supplement Market</Title>
      {marketData && (
        <ProductGrid>
          {marketData.supplement_name.map((name, index) => (
            <ProductCard key={index}>
              <ProductImage src={marketData.supplement_image[index]} alt={name} />
              <h3>{name}</h3>
              <p>For: {marketData.disease[index]}</p>
              <a href={marketData.buy[index]} target="_blank" rel="noopener noreferrer">Buy Now</a>
            </ProductCard>
          ))}
        </ProductGrid>
      )}
    </MarketContainer>
  );
};

export default Market;
