// src/pages/AIEngine.js
import React, { useState } from 'react';
import styled from 'styled-components';
import { submitImage } from '../api';

const AIEngineContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem 1rem;
`;

const Title = styled.h1`
  color: ${props => props.theme.colors.primary};
  text-align: center;
  margin-bottom: 2rem;
`;

const FileInput = styled.input`
  display: none;
`;

const FileInputLabel = styled.label`
  display: inline-block;
  background-color: ${props => props.theme.colors.secondary};
  color: white;
  padding: 0.8rem 1.5rem;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: ${props => props.theme.colors.primary};
  }
`;

const SubmitButton = styled.button`
  background-color: ${props => props.theme.colors.secondary};
  color: white;
  padding: 0.8rem 1.5rem;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: ${props => props.theme.colors.primary};
  }
`;

const AIEngine = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [result, setResult] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);
      const response = await submitImage(formData);
      setResult(response);
    }
  };

  return (
    <AIEngineContainer>
      <Title>AI Engine</Title>
      <FileInput type="file" id="file" onChange={handleFileChange} />
      <FileInputLabel htmlFor="file">Choose an image</FileInputLabel>
      <SubmitButton onClick={handleSubmit}>Analyze</SubmitButton>
      {result && (
        <div>
          <h2>{result.title}</h2>
          <p>{result.description}</p>
        </div>
      )}
    </AIEngineContainer>
  );
};

export default AIEngine;
