import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { MessageSquare, Send, X, Loader } from 'lucide-react';
import { sendChatMessage } from '../api';

const ChatbotContainer = styled(motion.div)`
  position: fixed;
  bottom: 20px;
  right: 20px;
  width: 350px;
  background-color: ${props => props.theme.colors.glass};
  border-radius: ${props => props.theme.borderRadius.large};
  box-shadow: ${props => props.theme.boxShadow.large};
  overflow: hidden;
  backdrop-filter: blur(10px);
  z-index: 9999;
  display: flex;
  flex-direction: column;
`;

const ChatHeader = styled.div`
  background-color: ${props => props.theme.colors.primary};
  color: white;
  padding: ${props => props.theme.spacing.md};
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-weight: bold;
`;

const ChatMessages = styled.div`
  height: 350px;
  overflow-y: auto;
  padding: ${props => props.theme.spacing.md};
  display: flex;
  flex-direction: column;
  background-color: rgba(255, 255, 255, 0.1);
`;

const MessageBubble = styled.div`
  max-width: 80%;
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  border-radius: ${props => props.theme.borderRadius.medium};
  margin-bottom: ${props => props.theme.spacing.sm};
  align-self: ${props => props.sender === 'You' ? 'flex-end' : 'flex-start'};
  background-color: ${props => props.sender === 'You' ? props.theme.colors.secondary : props.theme.colors.tertiary};
  color: ${props => props.theme.colors.text};
  box-shadow: ${props => props.theme.boxShadow.small};
`;

const ChatInputContainer = styled.div`
  display: flex;
  padding: ${props => props.theme.spacing.sm};
  background-color: rgba(255, 255, 255, 0.2);
`;

const ChatInput = styled.input`
  flex-grow: 1;
  padding: ${props => props.theme.spacing.sm};
  border: none;
  border-radius: ${props => props.theme.borderRadius.small};
  background-color: rgba(255, 255, 255, 0.3);
  color: ${props => props.theme.colors.text};

  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px ${props => props.theme.colors.secondary};
  }
`;

const SendButton = styled.button`
  background-color: ${props => props.theme.colors.secondary};
  color: white;
  border: none;
  border-radius: ${props => props.theme.borderRadius.small};
  padding: ${props => props.theme.spacing.sm};
  margin-left: ${props => props.theme.spacing.sm};
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: ${props => props.theme.colors.tertiary};
  }
`;

const ChatToggle = styled(motion.button)`
  position: fixed;
  bottom: 20px;
  right: 20px;
  background-color: ${props => props.theme.colors.primary};
  color: white;
  border: none;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  font-size: 24px;
  cursor: pointer;
  box-shadow: ${props => props.theme.boxShadow.medium};
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 9999;
`;

const TypingIndicator = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: ${props => props.theme.spacing.sm};
  color: ${props => props.theme.colors.textSecondary};
`;

const Chatbot = ({ diseaseContext = '' }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  useEffect(() => {
    if (isOpen && diseaseContext) {
      initializeChatbot(diseaseContext);
    }
  }, [isOpen, diseaseContext]);

  const addMessage = (sender, message, isStreaming = false) => {
    setMessages(prevMessages => {
      const lastMessage = prevMessages[prevMessages.length - 1];
      if (isStreaming && lastMessage && lastMessage.sender === sender) {
        return [...prevMessages.slice(0, -1), { ...lastMessage, message }];
      }
      return [...prevMessages, { sender, message }];
    });
  };

  const handleSend = async () => {
    if (input.trim()) {
      addMessage('You', input);
      setInput('');
      setIsTyping(true);
      try {
        const response = await fetch('/api/chat', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ message: input, disease_context: diseaseContext }),
        });

        const reader = response.body.getReader();
        const decoder = new TextDecoder();
        let aiResponse = '';

        while (true) {
          const { done, value } = await reader.read();
          if (done) break;
          const chunk = decoder.decode(value);
          aiResponse += chunk;
          addMessage('AI', aiResponse, true);
        }
      } catch (error) {
        console.error('Error:', error);
        addMessage('AI', 'Sorry, there was an error processing your request.');
      } finally {
        setIsTyping(false);
      }
    }
  };

  const initializeChatbot = async (context) => {
    setIsTyping(true);
    try {
      const initialMessage = `Tell me about ${context} disease in plants.`;
      addMessage('You', initialMessage);
      const response = await sendChatMessage(initialMessage, context);
      addMessage('AI', response);
    } catch (error) {
      console.error('Error:', error);
      addMessage('AI', 'Sorry, there was an error initializing the chatbot.');
    } finally {
      setIsTyping(false);
    }
  };

  return (
    <>
      <AnimatePresence>
        {isOpen && (
          <ChatbotContainer
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            transition={{ type: 'spring', stiffness: 500, damping: 30 }}
          >
            <ChatHeader>
              <span>Plant Health Assistant</span>
              <X onClick={() => setIsOpen(false)} style={{ cursor: 'pointer' }} />
            </ChatHeader>
            <ChatMessages>
              {messages.map((msg, index) => (
                <MessageBubble key={index} sender={msg.sender}>
                  <strong>{msg.sender}:</strong> {msg.message}
                </MessageBubble>
              ))}
              {isTyping && (
                <TypingIndicator>
                  <Loader size={16} style={{ marginRight: '8px' }} />
                  AI is typing...
                </TypingIndicator>
              )}
              <div ref={messagesEndRef} />
            </ChatMessages>
            <ChatInputContainer>
              <ChatInput
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSend()}
                placeholder="Type your message..."
              />
              <SendButton onClick={handleSend}>
                <Send size={20} />
              </SendButton>
            </ChatInputContainer>
          </ChatbotContainer>
        )}
      </AnimatePresence>
      <ChatToggle
        onClick={() => setIsOpen(!isOpen)}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        <MessageSquare />
      </ChatToggle>
    </>
  );
};

export default Chatbot;
