import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaGithub, FaLinkedin, FaEnvelope } from 'react-icons/fa';

const FooterWrapper = styled(motion.footer)`
  background-color: ${props => props.theme.colors.primary};
  color: white;
  padding: ${props => props.theme.spacing.xl} 0;
  text-align: center;
`;

const FooterContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const FooterLogo = styled.h2`
  font-size: 1.5rem;
  margin-bottom: ${props => props.theme.spacing.md};
`;

const FooterLinks = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: ${props => props.theme.spacing.md};
`;

const FooterLink = styled.a`
  color: white;
  text-decoration: none;
  margin: 0 ${props => props.theme.spacing.sm};
  font-size: 1.2rem;
  transition: color 0.3s ease;

  &:hover {
    color: ${props => props.theme.colors.secondary};
  }
`;

const FooterText = styled.p`
  font-size: 0.9rem;
  opacity: 0.8;
`;

const Footer = () => (
  <FooterWrapper
    initial={{ opacity: 0 }}
    animate={{ opacity: 1 }}
    transition={{ duration: 0.5 }}
  >
    <FooterContent>
      <FooterLogo>Plant Disease Detection</FooterLogo>
      <FooterLinks>
        <FooterLink href="https://github.com/yourusername" target="_blank" rel="noopener noreferrer">
          <FaGithub />
        </FooterLink>
        <FooterLink href="https://linkedin.com/in/yourusername" target="_blank" rel="noopener noreferrer">
          <FaLinkedin />
        </FooterLink>
        <FooterLink href="mailto:your.email@example.com">
          <FaEnvelope />
        </FooterLink>
      </FooterLinks>
      <FooterText>
        &copy; 2023 Plant Disease Detection. Created by Manthan Bhikadiya & Krishna Baldaniya
      </FooterText>
    </FooterContent>
  </FooterWrapper>
);

export default Footer;
